let productsarr = [];


function createProducts() {
  productsarr.push(new Product("Hat", "$25", "For cold winter days", 2, 101));
  productsarr.push(new Product("Shirt", "$20", "Comfortable", 2, 102));
  productsarr.push(new Product("Socks", "$10", "Great for running", 2, 103));

}


class Product {
  constructor(name, price, description, qty, product_id) {
    this.name = name;
    this.price = price;
    this.description = description;
    this.qty = qty;
    this.product_id = product_id;
  }
}


function addproduct(p) {
  productsarr.push(p);
}


function deleteProduct(id) {
  for (let i = 0; i < productsarr.length; i++) {
    if (productsarr[i].product_id == id) {
      productsarr.splice(i, 1);
      return 1;
    }

  }
  return -1
}


function getproducts() {
  return productsarr;
}


exports.Product = Product
exports.createProducts = createProducts
exports.addproduct = addproduct
exports.getproducts = getproducts
exports.deleteProduct = deleteProduct
